mapper.update:
UPDATE article
title = #{title}, `body` = #{body}, updateDate = NOW()
WHERE id = #{id}

mapper.select:
SELECT A.*, M.name AS extra__writer, B.name AS extra__boardName FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.id INNER JOIN board AS B ON A.boardId = B.id
WHERE A.title LIKE '%${keyword}%'
WHERE A.body LIKE '%${keyword}%'
WHERE M.name LIKE '%${keyword}%'
AND A.boardId = ${boardId}
ORDER BY id DESC LIMIT ${limitFrom}, ${viewArticleCount}

SELECT COUNT(*)
FROM article
WHERE boardId = ${boardId}
